from path_utils import outline_compatibility_report
for sl in font.selectedLayers:
    g = sl.parent
    rep = outline_compatibility_report(g)
    print(g.name)
    print('masters:', [m['masterName'] for m in rep['masters']])
    if rep['issues']:
        print('issues:')
        for iss in rep['issues']:
            print(' -', iss)
    else:
        print('No issues found')
